import React from 'react';
import Link from "next/link";
import Head from "next/head";
import Router from "next/router";

const Layout = ({ children, title, description, backButton }) => (
    <div>
        <Head>
            <title>{title}</title>
            <meta name="description" content={description} />
        </Head>
        <div className="container">
            <nav>
                {backButton && (
                    <span onClick={() => Router.back()} className="back-button">
                        &#x2b05;
                    </span>
                )}
                <Link href="/">
                    <a>
                        <span className="main-title">Just News</span>
                    </a>
                </Link>
            </nav>

            {children}
        </div>

        <style jsx>{`
            .container {
                max-width: 800px;
                margin: 0 auto;
                background: #f6f6ef;
            }
            nav {
                background: #63a5f1;
                padding: 1em;
            }
            nav > * {
                display: inline-block;
                color: #111;
            }
            nav a {
                text-decoration: none;
            }
            nav .main-title {
                font-weight: bold;
            }
            nav .back-button {
                font-size: 1rem;
                padding-right: 1em;
                cursor: pointer;
            }
        `}</style>
        <style global jsx>{`
            body {
                background: white;
                font-family: Verdana, Geneva, sans-serif;
            }
        `}</style>
    </div>
);

export default Layout;
